import Foundation

final class CartPresenter {

    // MARK: - Private properties -

    private unowned let view: CartViewInterface
    private let interactor: CartInteractorInterface
    private let wireframe: CartWireframeInterface
    
    private var products: [ProductModel]?

    // MARK: - Lifecycle -

    init(view: CartViewInterface, interactor: CartInteractorInterface, wireframe: CartWireframeInterface) {
        self.view = view
        self.interactor = interactor
        self.wireframe = wireframe
    }
    
    func viewDidLoad() {
        loadItems()
    }
    
    private func loadItems() {
        guard let id = UserManager.shared.loggedInUser?.id else {
            products = []
            view.reload()
            return
        }
        
        interactor.getProducts(customerId: id) { [weak self] result in
            switch result {
            case .success(let response):
                self?.products = response.data
                self?.view.reload()
            case .failure(let error):
                log.error(error.localizedDescription)
            }
        }
    }
}

// MARK: - Extensions -

extension CartPresenter: CartPresenterInterface {
    func orderButtonTapped() {
        guard let id = UserManager.shared.loggedInUser?.id else { return }
        interactor.updateCart(customerId: id) { [weak self] result in
            switch result {
            case .success(let cartResponse):
                if let cartId = cartResponse.cartId {
                    UserManager.shared.loggedInUser?.cartId = cartId
                    self?.products = []
                    self?.view.stopAnimation()
                    self?.view.showFinishOrder()
                }
            case .failure(let error):
                log.error(error.localizedDescription)
                self?.view.stopAnimation()
            }
        }
    }
    
    func getItem(row: Int) -> ProductModel {
        if let products = products, !products.isEmpty {
            return products[row]
        }
        return ProductModel()
    }
    
    func getProductsCount() -> Int {
        products?.count ?? 0
    }
    
    func removeProduct(row: Int) {
        if let orderId = products?[row].orderId {
            interactor.removeProduct(orderId: orderId) { [weak self] result in
                switch result {
                case .success(let response):
                    self?.products?.remove(at: row)
                    self?.view.removeProduct(row: row)
                case .failure(let error):
                    log.error(error.localizedDescription)
                }
            }
        }
    }
    
}
