import UIKit

final class ProductWireframe: BaseWireframe {

    weak var tabbarProtocol: CustomTabbarProtocol?
    // MARK: - Private properties -
    
    // MARK: - Module setup -

    init(product: ProductModel? = nil) {
        let moduleViewController = ProductViewController()
        super.init(viewController: moduleViewController)

        let interactor = ProductInteractor()
        let presenter = ProductPresenter(view: moduleViewController, interactor: interactor, wireframe: self, product: product)
        moduleViewController.presenter = presenter
    }

}

// MARK: - Extensions -

extension ProductWireframe: ProductWireframeInterface {
    func goToCart() {
        navigationController?.popViewController()
        tabbarProtocol?.goToTab(index: 2)
    }
    
}
